SELECT thesis.title, supervisor.first_name FROM thesis inner join supervisor on thesis.sup_id = supervisor.id inner join status on thesis.id = status.thesis_id INNER join student_writes_thesis on thesis.id = student_writes_thesis.thesis_id INNER join student on student_writes_thesis.stud_batch = student.batch and student_writes_thesis.stud_depart = student.department and student_writes_thesis.stud_rollno = student.class_rollno WHERE  concat(supervisor.first_name,supervisor.middle_name,supervisor.last_name) LIKE '%timil%' and thesis.title like '%title%' and status.final != '0000-00-00' and thesis.id = (Select distinct temp.thesis_id From thesis_has_area INNER join thesis_has_area temp on thesis_has_area.thesis_id = temp.thesis_id Where temp.area_id = 1 and thesis_has_area.area_id = 4)

SELECT student.first_name, student.middle_name, student.last_name, thesis.title, supervisor.first_name, supervisor.middle_name, supervisor.last_name, supervisor.title FROM thesis_has_area INNER join thesis on thesis_has_area.thesis_id = thesis.id inner join supervisor on thesis.sup_id = supervisor.id inner join status on thesis.id = status.thesis_id INNER join student_writes_thesis on thesis.id = student_writes_thesis.thesis_id INNER join student on student_writes_thesis.stud_batch = student.batch and student_writes_thesis.stud_depart = student.department and student_writes_thesis.stud_rollno = student.class_rollno WHERE concat(supervisor.title,supervisor.first_name,supervisor.middle_name,supervisor.last_name) LIKE '%'$supervisor'%' and thesis.title like '%'$title'%' and status.final != '0000-00-00' and thesis_has_area.area_id = 4;